using namespace std;
constexpr WORD FG_WHITE = FOREGROUND_RED | FOREGROUND_GREEN | FOREGROUND_BLUE | FOREGROUND_INTENSITY;

void cursor(int x, int y) {
	// Obtiene el handle de la consola de salida estándar
	HANDLE hCon = GetStdHandle(STD_OUTPUT_HANDLE);
	COORD coord;
	coord.X = static_cast<SHORT>(x);
	coord.Y = static_cast<SHORT>(y);
	SetConsoleCursorPosition(hCon, coord);
}
void aplicarColorPorOpcion(int opcion) {
	HANDLE hConsole = GetStdHandle(STD_OUTPUT_HANDLE);
	WORD bg = 0;

	switch (opcion) {
	case 1:  bg = 0;                                              break; // Black
	case 2:  bg = BACKGROUND_BLUE;                                break; // DarkBlue
	case 3:  bg = BACKGROUND_GREEN;                               break; // DarkGreen
	case 4:  bg = BACKGROUND_GREEN | BACKGROUND_BLUE;             break; // DarkCyan
	case 5:  bg = BACKGROUND_RED;                                 break; // DarkRed
	case 6:  bg = BACKGROUND_RED | BACKGROUND_BLUE;               break; // DarkMagenta
	case 7:  bg = BACKGROUND_RED | BACKGROUND_GREEN;              break; // DarkYellow
	case 8:  bg = BACKGROUND_RED | BACKGROUND_GREEN | BACKGROUND_BLUE;             break; // Gray
	case 9:  bg = BACKGROUND_INTENSITY;                           break; // DarkGray
	case 10: bg = BACKGROUND_BLUE | BACKGROUND_INTENSITY;         break; // Blue
	case 11: bg = BACKGROUND_GREEN | BACKGROUND_INTENSITY;        break; // Green
	case 12: bg = BACKGROUND_GREEN | BACKGROUND_BLUE | BACKGROUND_INTENSITY;        break; // Cyan
	case 13: bg = BACKGROUND_RED | BACKGROUND_INTENSITY;          break; // Red
	case 14: bg = BACKGROUND_RED | BACKGROUND_BLUE | BACKGROUND_INTENSITY;          break; // Magenta
	case 15: bg = BACKGROUND_RED | BACKGROUND_GREEN | BACKGROUND_INTENSITY;         break; // Yellow
	case 16: bg = BACKGROUND_RED | BACKGROUND_GREEN | BACKGROUND_BLUE | BACKGROUND_INTENSITY; // White
		break;
	default:
		bg = 0; // fallback a negro
	}

	SetConsoleTextAttribute(hConsole, FG_WHITE | bg);
}

