static void colorSegunValor(int v)
{
    switch (v) {
    case 1: color(8);   break;   // blanco/negro
    case 4: color(2);   break;   // linea amarilla
    case 9: color(13);  break;   // nodo rojo claro inicio
    case 5: color(7); break;//nodo amarillo final
    default: color(1); break;   // gris neutro para extras
    }
}

void imprimirMapaPeru(int x, int y)
{
    for (int f = 0; f < 50; ++f) {
        for (int c = 0; c < 102; ++c) {
            cursor(x + c, y);
            colorSegunValor(matrizPeru[f][c]);
            std::cout << ' ';
        }
        ++y; 
    }
    color(0);
}