static void color(int opcion)
{
    HANDLE h = GetStdHandle(STD_OUTPUT_HANDLE);
    switch (opcion) {
    case 0:  SetConsoleTextAttribute(h, FOREGROUND_RED | FOREGROUND_GREEN | FOREGROUND_BLUE | FOREGROUND_INTENSITY); break;
    case 1:  SetConsoleTextAttribute(h, BACKGROUND_RED | BACKGROUND_GREEN | BACKGROUND_BLUE); break;
    case 2:  SetConsoleTextAttribute(h, FOREGROUND_RED | FOREGROUND_GREEN | FOREGROUND_BLUE | BACKGROUND_BLUE); break;
    case 3:  SetConsoleTextAttribute(h, FOREGROUND_BLUE | BACKGROUND_BLUE | BACKGROUND_INTENSITY); break;
    case 4:  SetConsoleTextAttribute(h, FOREGROUND_RED | FOREGROUND_INTENSITY); break;
    case 5:  SetConsoleTextAttribute(h, FOREGROUND_GREEN | FOREGROUND_INTENSITY); break;
    case 6:  SetConsoleTextAttribute(h, FOREGROUND_RED | FOREGROUND_GREEN | FOREGROUND_INTENSITY); break;
    case 7:  SetConsoleTextAttribute(h, FOREGROUND_GREEN | FOREGROUND_BLUE | FOREGROUND_INTENSITY); break;
    case 8:  SetConsoleTextAttribute(h, FOREGROUND_RED | FOREGROUND_BLUE | FOREGROUND_INTENSITY); break;
    case 9:  SetConsoleTextAttribute(h, FOREGROUND_INTENSITY | BACKGROUND_GREEN | BACKGROUND_INTENSITY); break;
    case 10: SetConsoleTextAttribute(h, FOREGROUND_INTENSITY | BACKGROUND_RED | BACKGROUND_INTENSITY); break;
    case 11: SetConsoleTextAttribute(h, FOREGROUND_RED | FOREGROUND_GREEN | FOREGROUND_BLUE | FOREGROUND_INTENSITY | BACKGROUND_RED); break;
    case 12: SetConsoleTextAttribute(h, FOREGROUND_INTENSITY | BACKGROUND_RED | BACKGROUND_GREEN | BACKGROUND_INTENSITY); break;
    case 13: SetConsoleTextAttribute(h, FOREGROUND_RED | FOREGROUND_GREEN | FOREGROUND_BLUE | BACKGROUND_RED | BACKGROUND_BLUE); break;
    case 14: SetConsoleTextAttribute(h, FOREGROUND_INTENSITY | BACKGROUND_GREEN | BACKGROUND_BLUE | BACKGROUND_INTENSITY); break;
    case 15: SetConsoleTextAttribute(h, FOREGROUND_RED | FOREGROUND_GREEN | FOREGROUND_BLUE); break;
    default: SetConsoleTextAttribute(h, FOREGROUND_RED | FOREGROUND_GREEN | FOREGROUND_BLUE); break;
    }
}


